import React, { useState } from 'react';
import axios from 'axios';

function UploadForm() {
  const [excels, setExcels] = useState({
    combined_sources: null,
    official_instagram: null,
    official_facebook: null,
    keywords: null
  });
  const [image, setImage] = useState(null);
  const [customText, setCustomText] = useState("");
  const [date, setDate] = useState("");
  const [companyName, setCompanyName] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const validateExcelFile = (file) => {
    const allowedTypes = [
      'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
      'application/vnd.ms-excel'
    ];
    if (!allowedTypes.includes(file.type)) {
      throw new Error(`Invalid file type. Please upload an Excel file (.xlsx or .xls)`);
    }
  };

  const validateImageFile = (file) => {
    const allowedTypes = ['image/jpeg', 'image/png', 'image/gif'];
    if (!allowedTypes.includes(file.type)) {
      throw new Error('Invalid file type. Please upload an image file (JPEG, PNG, or GIF)');
    }
  };

  const handleExcelChange = (file, type) => {
    try {
      if (file) {
        validateExcelFile(file);
        setError("");
        // Keep original file name but ensure it has .xlsx extension
        const fileName = file.name.endsWith('.xlsx') ? file.name : `${type}.xlsx`;
        const newFile = new File([file], fileName, { type: file.type });
        setExcels(prev => ({
          ...prev,
          [type]: newFile
        }));
      }
    } catch (err) {
      setError(err.message);
    }
  };

  const handleImageChange = (file) => {
    try {
      if (file) {
        validateImageFile(file);
        setError("");
        setImage(file);
      }
    } catch (err) {
      setError(err.message);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    try {
      // Validate required files
      const missingFiles = Object.entries(excels)
        .filter(([_, file]) => !file)
        .map(([type]) => type);

      if (missingFiles.length > 0) {
        throw new Error(`Please upload all required Excel files: ${missingFiles.join(', ')}`);
      }

      if (!image) {
        throw new Error('Please upload an image');
      }

      if (!date) {
        throw new Error('Please select a date');
      }

      if (!companyName) {
        throw new Error('Please enter a company name');
      }

      const formData = new FormData();
      
      // Add Excel files
      Object.entries(excels).forEach(([_, file]) => {
        if (file) {
          formData.append("excel_files", file);
        }
      });

      // Add other form data
      formData.append("image", image);
      formData.append("custom_text", customText);
      formData.append("date", date);
      formData.append("company_name", companyName);

      const response = await axios.post("http://localhost:8000/generate-ppt/", formData, {
        responseType: 'blob',
        headers: {
          'Content-Type': 'multipart/form-data',
        }
      });

      // Handle successful response
      const contentType = response.headers['content-type'];
      if (contentType && contentType.includes('application/json')) {
        // Server returned an error in JSON format
        const reader = new FileReader();
        reader.onload = () => {
          try {
            const errorData = JSON.parse(reader.result);
            setError(errorData.detail || 'An error occurred while generating the presentation');
          } catch (e) {
            setError('An unexpected error occurred');
          }
        };
        reader.readAsText(response.data);
      } else {
        // Download file
        const blob = new Blob([response.data], { 
          type: "application/vnd.openxmlformats-officedocument.presentationml.presentation" 
        });
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = "report.pptx";
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        window.URL.revokeObjectURL(url);
      }
    } catch (err) {
      console.error('Error:', err);
      if (err.response?.data) {
        const reader = new FileReader();
        reader.onload = () => {
          try {
            const result = reader.result;
            try {
              const errorData = JSON.parse(result);
              setError(errorData.detail || 'An error occurred while generating the presentation');
            } catch (e) {
              setError(result || 'An error occurred while generating the presentation');
            }
          } catch (e) {
            setError('An unexpected error occurred');
          }
        };
        reader.readAsText(err.response.data);
      } else {
        setError(err.message || 'An error occurred while generating the presentation');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {error && <div style={{ color: 'red', marginBottom: '10px', padding: '10px', border: '1px solid red', borderRadius: '4px' }}>{error}</div>}
      
      <div style={{ marginBottom: '15px' }}>
        <label style={{ display: 'block', marginBottom: '5px' }}>Combined Sources Excel:</label>
        <input 
          type="file" 
          onChange={e => handleExcelChange(e.target.files[0], 'combined_sources')} 
          accept=".xlsx,.xls" 
          required 
        />
      </div>
      <div style={{ marginBottom: '15px' }}>
        <label style={{ display: 'block', marginBottom: '5px' }}>Official Instagram Excel:</label>
        <input 
          type="file" 
          onChange={e => handleExcelChange(e.target.files[0], 'official_instagram')} 
          accept=".xlsx,.xls" 
          required 
        />
      </div>
      <div style={{ marginBottom: '15px' }}>
        <label style={{ display: 'block', marginBottom: '5px' }}>Official Facebook Excel:</label>
        <input 
          type="file" 
          onChange={e => handleExcelChange(e.target.files[0], 'official_facebook')} 
          accept=".xlsx,.xls" 
          required 
        />
      </div>
      <div style={{ marginBottom: '15px' }}>
        <label style={{ display: 'block', marginBottom: '5px' }}>Keywords Excel:</label>
        <input 
          type="file" 
          onChange={e => handleExcelChange(e.target.files[0], 'keywords')} 
          accept=".xlsx,.xls" 
          required 
        />
      </div>
      <div style={{ marginBottom: '15px' }}>
        <label style={{ display: 'block', marginBottom: '5px' }}>Image:</label>
        <input 
          type="file" 
          onChange={e => handleImageChange(e.target.files[0])} 
          accept="image/*" 
          required 
        />
      </div>
      <div style={{ marginBottom: '15px' }}>
        <label style={{ display: 'block', marginBottom: '5px' }}>Date:</label>
        <input 
          type="date" 
          value={date} 
          onChange={e => setDate(e.target.value)} 
          required 
          style={{ padding: '5px' }}
        />
      </div>
      <div style={{ marginBottom: '15px' }}>
        <label style={{ display: 'block', marginBottom: '5px' }}>Company Name:</label>
        <input 
          type="text" 
          value={companyName} 
          onChange={e => setCompanyName(e.target.value)} 
          placeholder="Enter company name" 
          required 
          style={{ padding: '5px', width: '100%' }}
        />
      </div>
      <div style={{ marginBottom: '15px' }}>
        <label style={{ display: 'block', marginBottom: '5px' }}>Custom Text:</label>
        <input 
          type="text" 
          value={customText} 
          onChange={e => setCustomText(e.target.value)} 
          placeholder="Enter custom text" 
          style={{ padding: '5px', width: '100%' }}
        />
      </div>
      <button 
        type="submit" 
        disabled={loading}
        style={{
          padding: '10px 20px',
          backgroundColor: loading ? '#ccc' : '#007bff',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          cursor: loading ? 'not-allowed' : 'pointer'
        }}
      >
        {loading ? "Generating..." : "Generate PPT"}
      </button>
    </form>
  );
}

export default UploadForm;
