import React, { useState } from 'react';
import axios from 'axios';

function UploadForm() {
  const [excels, setExcels] = useState({
    combined_sources: null,
    official_instagram: null,
    official_facebook: null,
    keywords: null
  });
  const [image, setImage] = useState(null);
  const [customText, setCustomText] = useState("");
  const [date, setDate] = useState("");
  const [companyName, setCompanyName] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const handleExcelChange = (file, type) => {
    if (file) {
      // Ensure the file has the correct name
      const newFile = new File([file], `${type}.xlsx`, { type: file.type });
      setExcels(prev => ({
        ...prev,
        [type]: newFile
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    try {
      const formData = new FormData();
      
      // Add Excel files
      Object.entries(excels).forEach(([type, file]) => {
        if (file) {
          formData.append("excel_files", file);
        }
      });

      // Add other form data
      if (image) {
        formData.append("image", image);
      }
      formData.append("custom_text", customText);
      formData.append("date", date);
      formData.append("company_name", companyName);

      const response = await axios.post("http://localhost:8000/generate-ppt/", formData, {
        responseType: 'blob',
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // Download file
      const blob = new Blob([response.data], { 
        type: "application/vnd.openxmlformats-officedocument.presentationml.presentation" 
      });
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = "report.pptx";
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);
    } catch (err) {
      console.error('Error:', err);
      setError(err.response?.data?.detail || "An error occurred while generating the presentation");
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {error && <div style={{ color: 'red', marginBottom: '10px' }}>{error}</div>}
      
      <div>
        <label>Combined Sources Excel:</label>
        <input 
          type="file" 
          onChange={e => handleExcelChange(e.target.files[0], 'combined_sources')} 
          accept=".xlsx" 
          required 
        />
      </div>
      <div>
        <label>Official Instagram Excel:</label>
        <input 
          type="file" 
          onChange={e => handleExcelChange(e.target.files[0], 'official_instagram')} 
          accept=".xlsx" 
          required 
        />
      </div>
      <div>
        <label>Official Facebook Excel:</label>
        <input 
          type="file" 
          onChange={e => handleExcelChange(e.target.files[0], 'official_facebook')} 
          accept=".xlsx" 
          required 
        />
      </div>
      <div>
        <label>Keywords Excel:</label>
        <input 
          type="file" 
          onChange={e => handleExcelChange(e.target.files[0], 'keywords')} 
          accept=".xlsx" 
          required 
        />
      </div>
      <div>
        <label>Image:</label>
        <input 
          type="file" 
          onChange={e => setImage(e.target.files[0])} 
          accept="image/*" 
          required 
        />
      </div>
      <div>
        <label>Date:</label>
        <input 
          type="date" 
          value={date} 
          onChange={e => setDate(e.target.value)} 
          required 
        />
      </div>
      <div>
        <label>Company Name:</label>
        <input 
          type="text" 
          value={companyName} 
          onChange={e => setCompanyName(e.target.value)} 
          placeholder="Enter company name" 
          required 
        />
      </div>
      <div>
        <label>Custom Text:</label>
        <input 
          type="text" 
          value={customText} 
          onChange={e => setCustomText(e.target.value)} 
          placeholder="Enter custom text" 
        />
      </div>
      <button type="submit" disabled={loading}>
        {loading ? "Generating..." : "Generate PPT"}
      </button>
    </form>
  );
}

export default UploadForm;
